<?php    function microtime_float()    {        list($usec, $sec) = explode(" ", microtime());        return ((float)$usec + (float)$sec);    }        $start_time =  microtime_float();        /*        Element Framework         Index Redirector    */        include('config.php');    //Check if the user has a session Auth Level, If not Set the session defaults as Guest    if(!sess('auth_level'))    {        sess('username',"Guest");        sess('auth_level',0);     }    	//check the ip of the client has not changed	if(sess('user_ip')){	    if(sess('user_ip')!=$_SERVER['REMOTE_ADDR']){	        redirect("http://google.com");	    }	}        //Get default class name and function names from config    $myClass = app('default_class');         $myFunction = app('default_function') ;		    //if a class (controller) has been called	if (req("c"))    {        $class_name = req("c"); //sanitize        		//get the properties assigned to this class within the MENU table		$row = mysqli_fetch_array(query("select * from menu where class_name = '$class_name'"));        //If the users AUTH_LEVEL is high enough		if(sess("auth_level") >= $row["auth_level"]){                if (class_exists($class_name)) {                        //if the class exists, assign it						$myClass = $class_name;                }                if (req("f")){                        //if the function exists, assign it.						$function_name = req("f"); //sanitize;				                        if (method_exists($class_name,$function_name)) {                                        $myFunction = $function_name;                        }                }	        }    }		//run the class->function	eval("$"."$myClass->$myFunction();");	        //echo microtime_float() - $start_time;?>