<?    /*Element Framework        Core functions and includes    */    	//An easy little function to get application variables globally    function app($var){		global $app;		if(isset($app[$var]))		return $app[$var];	}        function newapp($myVar,$value=null){        global $app;        if(isset($value))        {            if($value == ""){                unset($app[$myVar]);            }else{                $app[$myVar] = $value;            }        }else{            if(isset($app[$myVar])){                return $app[$myVar];            }else{                return null;            }        }    }	/*Include Library Functions*/	$filePath = $app["lib_path"]."/functions/";	if ($handle = opendir($filePath)) {		while (false !== ($entry = readdir($handle))) {			if(is_file($filePath.$entry)){                include_once($filePath."$entry");			}		}		closedir($handle);	}    	/*Include Library Controllers*/	$filePath = $app["lib_path"]."/controllers/";	if ($handle = opendir($filePath)) {		while (false !== ($entry = readdir($handle))) {			if(is_file($filePath.$entry)){                include_once($filePath."$entry");			}		}		closedir($handle);	}		    /*Include Custom App Controllers*/    $filePath =app("app_path")."/controllers/";    if ($handle = opendir($filePath)) {        while (false !== ($entry = readdir($handle))) {            if(is_file($filePath.$entry)){                include_once($filePath."$entry");                $entry = str_replace(".php","",$entry);            }        }        closedir($handle);    }        /*Include Custom App Models*/    $filePath =app("app_path")."/models/";    if ($handle = opendir($filePath)) {        while (false !== ($entry = readdir($handle))) {            if(is_file($filePath.$entry)){                include_once($filePath."$entry");                $entry = str_replace(".php","",$entry);            }        }        closedir($handle);    }	    /*Include Custom App Functions*/    $filePath =app("app_path")."/functions/";    if ($handle = opendir($filePath)) {        while (false !== ($entry = readdir($handle))) {            if(is_file($filePath.$entry)){                include_once($filePath."$entry");            }        }        closedir($handle);    }			//connect to the database, we do this almost every time so lets just do it here.	db_connect();	//lets get started and initialize the session and some variables.	session_start();?>