<?    /*        Backup Function: Will Backup entire database and export as a .SQL file        Version: 14.2.22        Cody Joyce    */?><?    $backup = new backup();    class backup    {        function index()        {            $this->backup_tables(app("db_server"),app("db_username"),app("db_password"),app("db_database"));        }        function backup_tables($host,$user,$pass,$name,$tables = '*')        {            $link = mysqli_connect($host,$user,$pass);            mysqli_select_db($name,$link);            //get all of the tables            if($tables == '*')            {                $tables = array();                $result = mysqli_query('SHOW TABLES');                while($row = mysqli_fetch_row($result))                {                    $tables[] = $row[0];                }            }            else            {                $tables = is_array($tables) ? $tables : explode(',',$tables);            }            //cycle through            foreach($tables as $table)            {                $result = mysqli_query('SELECT * FROM '.$table);                $num_fields = mysqli_num_fields($result);                //$return.= 'DROP TABLE '.$table.';';                $row2 = mysqli_fetch_row(mysqli_query('SHOW CREATE TABLE '.$table));                $return.= "\n\n".$row2[1].";\n\n";                for ($i = 0; $i < $num_fields; $i++)                 {                    while($row = mysqli_fetch_row($result))                    {                        $return.= 'INSERT INTO '.$table.' VALUES(';                        for($j=0; $j<$num_fields; $j++)                         {                            $row[$j] = addslashes($row[$j]);                            $row[$j] = ereg_replace("\n","\\n",$row[$j]);                            if (isset($row[$j])) { $return.= '"'.$row[$j].'"' ; } else { $return.= '""'; }                            if ($j<($num_fields-1)) { $return.= ','; }                        }                        $return.= ");\n";                    }                }                $return.="\n\n\n";            }            $appname = str_replace(" ","-",app("name"));            header("Content-type: text/sql");            header("Content-Disposition: attachment; filename=$appname-".date("d-m-y",time()).".sql");            header("Pragma: no-cache");            header("Expires: 0");		             echo $return;        }    }?>